import { useState, useRef } from "react"

export default function Calculator(){

    const [inputVal,setVal] =useState<string>("");
      const inputRef = useRef<string>("");
     const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
      const value = e.currentTarget.value;

    // Update state and ref
    setVal((prev) => {
      const updated = prev + value;
      inputRef.current = updated; // Keep latest value in ref
      return updated;
    });
  };

    return(
        <>
        <h2> Calculator</h2>
        <div style={{paddingTop:'30px'}}>
        <input type="text" value={inputVal} />
        <div style={{paddingTop:'30px'}}></div>
       
        
            <button value={7} type="button" onClick={handleClick}>7</button>
              <button value={8} type="button"  onClick={handleClick}>8</button>
              <button value={9} type="button" onClick={handleClick}>9</button>
               <button value='+' type="button" onClick={handleClick}>+</button>
        <br />
             <button value={4} type="button">4</button>
             <button value={5} type="button">5</button>
              <button value={6} type="button">6</button>
               <button value='-' type="button">-</button>
               <br />
             <button value={1} type="button">1</button>
             <button value={2} type="button">2</button>
              <button value={3} type="button">3</button>
               <button value='*' type="button">*</button>
               <br />
             <button value={7} type="button">C</button>
             <button value={0} type="button">0</button>
              <button value='=' type="button" onClick={finalCalculation}>=</button>
               <button value='/' type="button">/</button>
        </div>



      
        </>
    )
}
